import './App.css';

function style(backgroundColor, textColor = 'black') {
    return {
        'background-color': backgroundColor,
        'color': textColor
    }
}

/** styling icons  */
export function iconStyle(lineCode) {
    switch (lineCode) {
        case 'Yellow-N':
        case 'Yellow-S':
        case 'Yellow':
            return style('#FAE64D')
        case 'Green-N':
        case 'Green-S':
        case 'Green':
            return style('#6BB557')
        case 'Orange-N':
        case 'Orange-S':
        case 'Orange':
            return style('#EEA941')
        case 'C':
        case 'F':
        case 'PM':
        case 'PH':
            return {
                'background-color': 'rgb(180, 154, 54)',
                'color': 'black'
            }
        case 'N-OWL':
        case 'N OWL':
        case 'L-OWL':
            return {
                'background-color': 'rgb(102, 102, 102)',
                'color': 'white'
            }

        case 'KT':
            return {
                'background-color': 'black',
                'color': 'rgb(255, 255, 255)'

            }
        case 'M':
            return {
                'background-color': 'rgb(0, 133, 71)',
                'color': 'rgb(255, 255, 255)'
            }
        case 'J':
            return {
                'background-color': '#a96614',
                'color': '#fff'

            }
        case 'S':
            return {
                'background-color': '#ffd600',
                'color': '#000'
            }
        default:
            return {
                'background-color': 'rgb(0, 91, 149)',
                'color': 'rgb(255, 255, 255)'
            }
    }
}
