import './App.css';

import React, { Component, useState } from 'react'
import Select, { InputActionMeta } from 'react-select'
import opts from './options.json'

const options = opts.options



function App() {
  const [choice, setChoice] = useState(options[0])
  const [lastUpdateTime, setUpdateTime] = useState(Date.now())

  function onChange(valueMeta, actionMeta) {
    console.log(actionMeta.action);
    switch (actionMeta.action) {
      case 'select-option':
      case 'set-value':
        setChoice(valueMeta);
    }
  }

  function ArrivalRow(props) {
    return <div className='ArrivalRow'></div>;
  }

  const StopSelector = () => (
    <Select id="StopSelector" options={options} onChange={onChange} />
  );

  const ToTimeStr = (date) => (new Date(lastUpdateTime).toLocaleTimeString([], { hour: 'numeric', minute: '2-digit' }));

  return (
    <div id="app">
      <header>
        <p>
          Upcoming trains/buses at station {choice.label}. Last updated at {ToTimeStr(lastUpdateTime)}. </p>

        <StopSelector></StopSelector>

        <div>
          <i style={{ 'background-color': 'rgb(0, 91, 149)', 'color': 'rgb(255, 255, 255)' }} className='route'>30</i>
        </div>
      </header>
    </div >
  );
}

export default App;
