import './App.css';

import React, { Component, useState } from 'react'
import Select, { InputActionMeta } from 'react-select'
import opts from './options.json'

const options = opts.options



function App() {
  const [choice, setChoice] = useState(options[0].value)

  function onChange(valueMeta, actionMeta) {
    console.log(actionMeta.action);
    switch (actionMeta.action) {
      case 'select-option':
      case 'set-value':
        setChoice(valueMeta.value);
    }
  }

  const FlavorSelector = () => (
    <Select options={options} onChange={onChange} defaultInputValue={options[0].label} />
  )


  return (
    <div>
      <header>
        <p>
          You selected {choice}.
        </p>

        <FlavorSelector></FlavorSelector>
      </header>
    </div>
  );
}

export default App;
