import './App.css';

import React, { Component, useState } from 'react'
import Select, { InputActionMeta } from 'react-select'
import opts from './options.json'

const options = opts.options



function App() {
  const [choice, setChoice] = useState(options[0])
  const [lastUpdateTime, setUpdateTime] = useState(Date.now())

  function onChange(valueMeta, actionMeta) {
    console.log(actionMeta.action);
    switch (actionMeta.action) {
      case 'select-option':
      case 'set-value':
        setChoice(valueMeta);
    }
  }

  const FlavorSelector = () => (
    <Select options={options} onChange={onChange} />
  );

  const ToTimeStr = (date) => (new Date(lastUpdateTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true }));

  return (
    <div id="app">
      <header>
        <p>
          Upcoming trains at station {choice.label}. Last updated at {ToTimeStr(lastUpdateTime)}. </p>

        <FlavorSelector></FlavorSelector>
      </header>
    </div>
  );
}

export default App;
