import './App.css';

import React, { Component, useState } from 'react'
import Select, { InputActionMeta } from 'react-select'
import opts from './options.json'

const options = opts.options



function App() {
  const [choice, setChoice] = useState(options[0].value)

  function onChange(value, action_and_prevvalue) {
    console.log(action_and_prevvalue.action);
    switch (action_and_prevvalue.action) {
      case 'select-option':
      case 'set-value':
        setChoice(value);
        return value;
    }
  }

  const FlavorSelector = () => (
    <Select options={options} onInputChange={onChange} />
  )


  return (
    <div>
      <header>
        <p>
          You selected {choice}.
        </p>

        <FlavorSelector></FlavorSelector>
      </header>
    </div>
  );
}

export default App;
