import './App.css';

import React, { Component, useState } from 'react'
import Select from 'react-select'
import opts from './options.json'

const options = opts.options



function App() {
  const [choice, setChoice] = useState(options[0])

  function onChange(value, action) {
    switch (action) {
      case 'inputChange':
        console.log("input changed");
        setChoice(value);
        return value;
    }
  }

  const FlavorSelector = () => (
    <Select options={options} onInputChange={onChange} />
  )


  return (
    <div>
      <header>
        <p>
          You selected {choice.label}.
        </p>

        <FlavorSelector></FlavorSelector>
      </header>
    </div>
  );
}

export default App;
